main=()=>{open_menu();handle_qa();smooth_scroll_and_offset()}
smooth_scroll_and_offset=()=>{const links=document.querySelectorAll('a[href^="#"]');const offset=100;links.forEach(link=>{link.addEventListener('click',function(e){e.preventDefault();const targetId=this.getAttribute('href').substring(1);const targetElement=document.getElementById(targetId);if(targetElement){const elementPosition=targetElement.getBoundingClientRect().top+window.pageYOffset;const offsetPosition=elementPosition-offset;window.scrollTo({top:offsetPosition,behavior:'smooth'})}})})}
handle_qa=()=>{const faqQuestions=document.querySelectorAll('.faq-question');faqQuestions.forEach((question)=>{const title=question.querySelector('.faq-question__title');title.addEventListener('click',()=>{question.classList.toggle('is-open');faqQuestions.forEach((otherQuestion)=>{if(otherQuestion!==question){otherQuestion.classList.remove('is-open')}})})})}
open_menu=()=>{const toggle=document.querySelector('.menu-navigation__toggle');const menuNavigation=document.querySelector('.menu-navigation');if(toggle&&menuNavigation){toggle.addEventListener('click',()=>{menuNavigation.classList.toggle('open')})}else{console.error('Élément .menu-navigation__toggle ou .menu-navigation non trouvé.')}}
document.addEventListener('DOMContentLoaded',()=>{main()})

class SimpleSlider{constructor(sliderElement){this.slider=sliderElement;this.wrapper=this.slider.querySelector('.slider-wrapper');this.slides=Array.from(this.wrapper.children);this.prevButton=this.slider.querySelector('.slider-button.prev');this.nextButton=this.slider.querySelector('.slider-button.next');this.currentIndex=0;this.init()}
init(){this.updateSlider();this.addEventListeners()}
addEventListeners(){this.nextButton.addEventListener('click',()=>this.goToSlide(this.currentIndex+1));this.prevButton.addEventListener('click',()=>this.goToSlide(this.currentIndex-1))}
goToSlide(index){if(index<0){this.currentIndex=this.slides.length-1}else if(index>=this.slides.length){this.currentIndex=0}else{this.currentIndex=index}
this.updateSlider()}
updateSlider(){const offset=-this.currentIndex*100;this.wrapper.style.transform=`translateX(${offset}%)`}}
document.addEventListener('DOMContentLoaded',()=>{const sliderElement=document.querySelector('.carousel-img__carousel');if(sliderElement){new SimpleSlider(sliderElement)}})